import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Payment = () => {
    const userString = localStorage.getItem("user");
    const userObject = JSON.parse(userString);
    const name = userObject.name;
    const [bills, setBills] = useState({
        mainbill: null,
        elecbill: null,
        waterbill: null
    });
    const [totalBill, setTotalBill] = useState(0);

    useEffect(() => {
        getBills();
        return () => {
            // Reset bills when the component unmounts or when the user leaves the page
            setBills({
                mainbill: null,
                elecbill: null,
                waterbill: null
            });
            setTotalBill(0);
        };
    }, []);

    useEffect(() => {
        // Calculate total bill whenever individual bill amounts change
        let total = 0;
        if (bills.mainbill !== null) total += parseFloat(bills.mainbill);
        if (bills.elecbill !== null) total += parseFloat(bills.elecbill);
        if (bills.waterbill !== null) total += parseFloat(bills.waterbill);
        setTotalBill(total);
    }, [bills]);

    const getBills = async () => {
        let result = await fetch(`http://localhost:5000/payment/${name}`);
        result = await result.json();
        console.log(result);

        // Iterate over the result array and update the bills state accordingly
        result.forEach(item => {
            if (item.billcategory === "maintenance") {
                setBills(prevState => ({ ...prevState, mainbill: item.billamount }));
            } else if (item.billcategory === "electricity") {
                setBills(prevState => ({ ...prevState, elecbill: item.billamount }));
            } else if (item.billcategory === "water") {
                setBills(prevState => ({ ...prevState, waterbill: item.billamount }));
            }
        });
    }

    const navigate = useNavigate();

    const handlePayment = async () => {
        alert(`PAID ${totalBill}`);
        navigate("/events");
    }

    const handleCheckboxChange = (e) => {
        const { id, checked } = e.target;
        // Update bills state based on checkbox changes
        setBills(prevState => ({ ...prevState, [id]: checked ? 0 : null }));
    }

    return (
        <div>
            <h1 className="tag">Your Dues, {name}</h1>
            <br />
            <div className="container">
                <div className="box1">
                    <h1>Compulsory Bills</h1>
                    <h2>
                        <label><input type="checkbox" id="maintenance" onChange={handleCheckboxChange} /> Maintenance Bill ({bills.mainbill})</label>
                        <label><input type="checkbox" id="electricity" onChange={handleCheckboxChange} /> Electricity Bill ({bills.elecbill})</label>
                        <label><input type="checkbox" id="water" onChange={handleCheckboxChange} /> Water Bill ({bills.waterbill})</label>
                    </h2>

                    <button id="payBtn" className="btn" onClick={handlePayment}>Pay Now (${totalBill.toFixed(2)})</button>
                </div>
                {/* Additional amenities section */}
            </div>
        </div>
    )
}

export default Payment;
