import React from "react";

const Payment=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    const name = userObject.name;
    const email = userObject.email;
    const apartment_no = userObject.apartment_no;
    const phone_no = userObject.phone_no;
    const handlePayment=async()=>{
        alert("PAID")

    }
    return(
        <div>
            <h1 className="tag">Pay Your Bills, {name}</h1>
            <br></br>
            <div className="container">
            
                <div className="box">

                </div>
                <div className="box">
                    <h1>Additional Amenties</h1>
                    <h2>Select Amenities</h2>
                    <h2><label><input type="checkbox" id="gym"/> Gym</label><br/>
                    <label><input type="checkbox" id="pool"/> Pool</label><br/>
                    <label><input type="checkbox" id="parking"/> Parking</label></h2>
                    <br/>
                    <h2>Select Payment Option</h2>
                    <h2><label><input type="radio" name="payment" value="monthly" id="monthly"/> Monthly</label><br/>
                    <label><input type="radio" name="payment" value="yearly" id="yearly"/> Yearly</label></h2>
                    <br/>
                    <button id="payBtn" class="btn" onclick={handlePayment}>Pay Now</button>
                </div>
            
        </div>
        </div>
    )
}

export default Payment;
