import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router-dom";

const AddProduct=()=>{
    const [name,setName]=useState("");
    const [price,setPrice]=useState("");
    const [category,setCategory]=useState("");
    const [company,setPassword]=useState("");
    
    const navigate=useNavigate();
    useEffect(()=>{
        const auth=localStorage.getItem('user');
        if(auth){
            navigate('/')
        }
    })

    const collectData=async()=>{
        console.warn(name,email,password);
        let result=await fetch('http://localhost:5000/register',{
            method:'POST',
            body:JSON.stringify({name,email,password}),
            headers:{
                'Content-Type':'application/json'
            }
        });

        result=await result.json();
        console.warn(result);
        localStorage.setItem("user",JSON.stringify(result));
        navigate('/')

    }
    return(
        <div>
            <h1>
                Add Products
                <input className="inputbox" type="text" placeholder="Enter Product Category"></input>
                <input className="inputbox" type="text" placeholder="Enter Product Brand"></input>
                <input className="inputbox" type="text" placeholder="Enter Product Name"></input>
                <input className="inputbox" type="text" placeholder="Enter Product Price"></input>
                <button className="button" onClick={collectData}>SUBMIT</button>
            </h1>
        </div>
        
    )
}

export default AddProduct;
