import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const UpdateProduct = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [category, setCategory] = useState("");
  const [brand, setBrand] = useState("");

  const params = useParams();

  useEffect(() => {
    const getProductDetails = async () => {
      console.warn(params);
      let result = await fetch(`http://localhost:3000/product/${params.id}`);
      result = await result.json();
      console.warn(result);

      setName(result.name);
      setPrice(result.price);
      setCategory(result.category);
      setBrand(result.brand);
    };

    getProductDetails(); // Call getProductDetails here

    // Include getProductDetails in the dependency array
  }, [params.id]); // Adding params.id to the dependency array

  return (
    <div>
      <h1>
        Update Products
        <input
          className="inputbox"
          type="text"
          placeholder="Enter Product Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        ></input>

        <input
          className="inputbox"
          type="text"
          placeholder="Enter Product Brand"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        ></input>

        <input
          className="inputbox"
          type="text"
          placeholder="Enter Product Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        ></input>

        <input
          className="inputbox"
          type="text"
          placeholder="Enter Product Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        ></input>

        <button className="button" onClick={getProductDetails}>
          SUBMIT
        </button>
      </h1>
    </div>
  );
};

export default UpdateProduct;
