import React,{useEffect, useState} from "react";
import { useNavigate } from "react-router-dom";
const Payment=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    let name = userObject.name;
    let mainbill;
    let elecbill;
    let waterbill;
    // const email = userObject.email;
    // const apartment_no = userObject.apartment_no;
    // const phone_no = userObject.phone_no;
    const [bills, setBills] = useState({
        mainbill: 0,
        elecbill: 0,
        waterbill: 0
    });
    
    useEffect(() => {
        getBills();
    },[]);

    const getBills = async () => {
        let result = await fetch(`http://localhost:5000/payment/${name}`);
        result = await result.json();
        console.log(result);

        result.forEach(item => {
            if (item.billcategory === "maintenance") {
                setBills(prevState => ({ ...prevState, mainbill: item.billamount }));
            } else if (item.billcategory === "electricity") {
                setBills(prevState => ({ ...prevState, elecbill: item.billamount }));
            } else if (item.billcategory === "water") {
                setBills(prevState => ({ ...prevState, waterbill: item.billamount }));
            }
        });
    }

    const navigate=useNavigate()
    const handlePayment=async()=>{
        
        alert("PAID")
        navigate("/events")
    }

    const handleCheckboxChange = (e) => {
        const { id, checked } = e.target;
        // Update bills state based on checkbox changes
        setBills(prevState => ({ ...prevState, [id]: checked ? 0 : null }));
    }
    return(
        <div>
            <h1 className="tag">Your Dues, {name}</h1>
            <br></br>
            <div className="container">
            
                <div className="box1">
                <h1>Compulsory Bills</h1>
                    <h2>
                    <label><input type="checkbox" id="maintenance" onClick={handleCheckboxChange}/> Maintenance Bill ({bills.mainbill} INR)</label>
                    <label><input type="checkbox" id="electricity" onClick={handleCheckboxChange}/> Electricity Bill ({bills.elecbill} INR)</label>
                    <label><input type="checkbox" id="water" onClick={handleCheckboxChange}/> Water Bill ({bills.waterbill} INR)</label>
                    </h2>
                    
                    <button id="payBtn" class="btn" onClick={handlePayment}>Pay Now</button>

                </div>
                <div className="box1">
                    <h1>Additional Amenties</h1>
                    <h2>Select Amenities</h2>
                    <h2><label><input type="checkbox" id="gym"/> Gym </label>
                    <label><input type="checkbox" id="pool"/> Pool</label>
                    <label><input type="checkbox" id="parking"/> Parking</label></h2>
                    
                    <h2>Select Payment Option</h2>
                    <h2><label><input type="radio" name="payment" value="monthly" id="monthly"/> Monthly</label>
                    <label><input type="radio" name="payment" value="yearly" id="yearly"/> Yearly</label></h2>
                    
                    <button id="payBtn" class="btn" onClick={handlePayment}>Pay Now</button>
                </div>
            
        </div>
        </div>
    )
}

export default Payment;
