import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Payment = () => {
    const userString = localStorage.getItem("user");
    const userObject = JSON.parse(userString);
    const name = userObject.name;

    const [bills, setBills] = useState({
        mainbill: 0,
        elecbill: 0,
        waterbill: 0
    });

    useEffect(() => {
        getBills();
    }, []);

    const getBills = async () => {
        let result = await fetch(`http://localhost:5000/payment/${name}`);
        result = await result.json();
        console.log(result);

        result.forEach(item => {
            if (item.billcategory === "maintenance") {
                setBills(prevState => ({ ...prevState, mainbill: item.billamount }));
            } else if (item.billcategory === "electricity") {
                setBills(prevState => ({ ...prevState, elecbill: item.billamount }));
            } else if (item.billcategory === "water") {
                setBills(prevState => ({ ...prevState, waterbill: item.billamount }));
            }
        });
    }

    const navigate = useNavigate();

    const handlePayment = async () => {
        alert("PAID");

        // Delete bill details from the database for the selected bills
        if (bills.mainbill > 0) {
            await deleteBill("maintenance");
        }
        if (bills.elecbill > 0) {
            await deleteBill("electricity");
        }
        if (bills.waterbill > 0) {
            await deleteBill("water");
        }

        // Reset bill amounts after payment
        setBills({
            mainbill: 0,
            elecbill: 0,
            waterbill: 0
        });

        navigate("/events");
    }

    const deleteBill = async (category) => {
        await fetch(`http://localhost:5000/payment/${name}/${category}`, {
            method: 'DELETE'
        });
    }

    return (
        <div>
            <h1 className="tag">Your Dues, {name}</h1>
            <br />
            <div className="container">
                <div className="box1">
                    <h1>Compulsory Bills</h1>
                    <h2>
                        <label>
                            <input type="checkbox" id="maintenance" checked={bills.mainbill > 0} onChange={() => setBills(prevState => ({ ...prevState, mainbill: prevState.mainbill > 0 ? 0 : 100 }))} /> Maintenance Bill ({bills.mainbill} INR)
                        </label>
                        <label>
                            <input type="checkbox" id="electricity" checked={bills.elecbill > 0} onChange={() => setBills(prevState => ({ ...prevState, elecbill: prevState.elecbill > 0 ? 0 : 150 }))} /> Electricity Bill ({bills.elecbill} INR)
                        </label>
                        <label>
                            <input type="checkbox" id="water" checked={bills.waterbill > 0} onChange={() => setBills(prevState => ({ ...prevState, waterbill: prevState.waterbill > 0 ? 0 : 50 }))} /> Water Bill ({bills.waterbill} INR)
                        </label>
                    </h2>
                    <button className="btn" onClick={handlePayment}>Pay Now</button>
                </div>
            </div>
        </div>
    )
}

export default Payment;
