                <ul key={item._id}>
                    <li>{index + 1}</li>
                    <li>{item.category}</li>
                    <li>{item.brand}</li>
                    <li>{item.name}</li>
                    <li>{item.price}</li>
                    <li><button onClick={()=>deleteData(item._id)} className="button">DELETE</button>
                    <Link to={"/update-product/"+item._id}>Update</Link>
                    </li>
                </ul>
            ))
        : <h1>No Product Found</h1>}
        </div>

    )
}

export default Votetocand;
