import React,{useState ,useEffect} from "react";
import { useNavigate } from "react-router-dom";

const Ticket=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    const name = userObject.name;
    const [complaint,setComplaint]=useState("");
    const [ticket, setTicket] = React.useState([]);

    useEffect(() => {
        getTickets();
    },[]);

    const getTickets=async ()=>{
        let result = await fetch(`http://localhost:5000/ticket/${name}`);
        result = await result.json();
        setTicket(result)  
        console.log(result);
    }

    
    const navigate=useNavigate();

    const handlecomplaint=async()=>{
        let result=await fetch('http://localhost:5000/ticket',{
            method:'POST',
            body:JSON.stringify({username,complaint}),
            headers:{
                'Content-Type':'application/json'
            }
        });
        
        result=await result.json();
        console.log(result);
        alert("ticket is raised")
        setTicket(result);
        // navigate('/events')
        
    }

    const handelclick=()=>{

    }

    return(
        <div>
            <h1>Raise your Tickets here</h1>
            <textarea className="inputbox" type="text" placeholder="Enter your Grievance here"
            value={complaint} onChange={(e)=>setComplaint(e.target.value)}/>
            <button onClick={handlecomplaint} className="button">SUBMIT</button>

            <br/>
            <br/>

            <div className="product-list" >
            {ticket.length>0 ? ticket.map((item, index) => (
                <div><ul>
                    <h1>Tickets Raised:</h1>
                <li>S.No</li>
                <li>Tickets</li>
                <li>Action</li>
            </ul>
                <ul key={item._id}>
                    <li>{index + 1}</li>
                    <li>{item.complaint}</li>
                    <li><button onClick={handelclick}>VOTE</button></li>
                </ul>
                </div>
            ))
        : <h1>No Tickets Raised</h1>}
        </div>

        </div>
    )
}

export default Ticket;
