import React,{useState} from "react";
import { useNavigate } from "react-router-dom";
const Voting=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    const userName = userObject.name;
    const navigate=useNavigate();
    const [vote,set_vote]=useState("");
    const [pitch,setPitch]=useState("");

    const handlevote=async()=>{
        let result=await fetch('http://localhost:5000/voting',{
            method:'POST',
            body:JSON.stringify({userName,vote}),
            headers:{
                'Content-Type':'application/json'
            }
        });

        result=await result.json();
        console.warn(result);
        localStorage.setItem("user",JSON.stringify(result));
        navigate('/');
    }

    const handleregister=()=>{
        console.log("hello");
        alert("you are registered as a candidate");
        navigate('/events')
    }
    return(
        <div>
        <div>
            <h1 className="tag">HELLO {userName} </h1>
        </div>

        <div className="container">
            <div className="box">
            <h2>
            <form className="voteform">
                Choose a Candidate: 
                <br></br>
                <select id="candidates" name="candidates" className="idselect   ">
                    <option className="option" value="Shivam Garg">Shivam Garg</option>
                    <option className="option" value="Yash Singh">Yash Singh</option>
                    <option className="option" value="Gautam Prakash">Gautam Prakash</option>
                    <option className="option" value="Sonu Chaudhary">Sonu Chaudhary</option>
                </select>
                <input onClick={handlevote} type="submit" className="button" value="Submit"/>
                </form>
            
            </h2>
            </div>
            <div className="box">
                <h2>
                    <br></br>
                    Register as a Candidate:
                    <textarea className="inputboxpitch" type="text" placeholder="Enter your Pitch here"
                        value={pitch} onChange={(e)=>setPitch(e.target.value)}/>
                    <button onClick={handleregister} className="button">SUBMIT</button>
                </h2>
            </div>
        </div>
       </div>

        





        
        
    )
}

export default Voting;
