import React,{useState} from "react";
import { useNavigate } from "react-router-dom";
const Voting=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    const name = userObject.name;
    const email = userObject.email;
    const apartment_no = userObject.apartment_no;
    const phone_no = userObject.phone_no;
    

    const navigate=useNavigate();
    // const [vote,set_vote]=useState("");
    const [pitch,setPitch]=useState("");

    // const handlevote=async()=>{
    //     const votedcandidate = userObject;
    //     let result=await fetch('http://localhost:5000/voting',{
    //         method:'POST',
    //         body:JSON.stringify({name,email,phone_no,apartment_no,votedcandidate,pitch}),
    //         headers:{
    //             'Content-Type':'application/json'
    //         }
    //     });

    //     alert("You are Registered as a Candidate");
    //     navigate('/events');
    // }

    const handleregister=async()=>{
        const votedcandidate = userObject.name;
        let result=await fetch('http://localhost:5000/voting',{
            method:'POST',
            body:JSON.stringify({name,email,phone_no,apartment_no,votedcandidate,pitch}),
            headers:{
                'Content-Type':'application/json'
            }
        });
        console.log(result);

        alert("You are Registered as a Candidate");
        navigate('/events')
    }
    return(
        <div>
        <div>
            <h1 className="tag">Hello, {name} </h1>
        </div>

        <div className="container">
            {/* REGISTER FOR A CANDIDATE */}
            <div className="box">
            <div className="product-list">
            <h1>Products Are Listed Below:</h1>
            <input onChange={searchproduct}
             className="searchinputbox" type="text" placeholder="Search Anything..."></input>
            <ul>
                <li>S.No</li>
                <li>Name</li>
                <li>Address</li>
                <li>Name</li>
                <li>Price</li>
                <li>Action</li>
            </ul>

            {products.length>0 ? products.map((item, index) => (
                <ul key={item._id}>
                    <li>{index + 1}</li>
                    <li>{item.category}</li>
                    <li>{item.brand}</li>
                    <li>{item.name}</li>
                    <li>{item.price}</li>
                </ul>
            ))
        : <h1>No Product Found</h1>}
        </div>
            </div>

            {/* REGISTER AS A CANDIDATE */}
            <div className="box">
                <h2>
                    <br></br>
                    Register as a Candidate:
                    <textarea className="inputboxpitch" type="text" placeholder="Enter your Pitch here"
                        value={pitch} onChange={(e)=>setPitch(e.target.value)}/>
                    <button onClick={handleregister} className="button">SUBMIT</button>
                </h2>   
            </div>
        </div>
       </div>

    )
}

export default Voting;
