import React, { useState } from "react";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [showLoginForm, setShowLoginForm] = useState(true); // State to track whether to show login form or signup form

    const toggleForm = () => {
        setShowLoginForm(!showLoginForm); // Toggle the state between true and false
    }

    const handleLogin = (e) => {
        e.preventDefault();
        console.log("Login form submitted with email:", email, "and password:", password);
        // Implement your login logic here
    }

    const handleSignup = (e) => {
        e.preventDefault();
        console.log("Signup form submitted");
        // Implement your signup logic here
    }

    return (
        <div className="form-container" id="formContainer">
            <form id="loginForm" className="form-content" style={{ display: showLoginForm ? 'block' : 'none' }} onSubmit={handleLogin}>
                <h2>Login</h2>
                <div className="input-group">
                    <input type="email" id="loginEmail" placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div className="input-group">
                    <input type="password" id="loginPassword" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <button type="submit" className="btn">Login</button>
                <p className="form-toggle">Don't have an account? <button onClick={toggleForm}>Sign Up</button></p>
            </form>

            <form id="signupForm" className="form-content" style={{ display: showLoginForm ? 'none' : 'block' }} onSubmit={handleSignup}>
                <h2>Sign Up</h2>
                <div className="input-group">
                    <input type="text" id="signupName" placeholder="Full Name" required />
                </div>
                <div className="input-group">
                    <input type="email" id="signupEmail" placeholder="Email Address" required />
                </div>
                <div className="input-group">
                    <input type="tel" id="signupPhone" placeholder="Phone Number" required pattern="[0-9]{10}" />
                </div>
                <div className="input-group">
                    <input type="text" id="signupApartmentNo" placeholder="Apartment Number" required />
                </div>
                <div className="input-group">
                    <input type="password" id="signupPassword" placeholder="Create Password" required />
                </div>
                <button type="submit" className="btn">Sign Up</button>
                <p className="form-toggle">Already have an account? <button onClick={toggleForm}>Login</button></p>
            </form>
        </div>
    )
}

export default Login;
