import React,{useState,useEffect} from "react";
import { useNavigate } from "react-router-dom";
const Votetocand=()=>{
    const userString = localStorage.getItem("user"); 
    const userObject = JSON.parse(userString); 
    const name = userObject.name;
    const email = userObject.email;
    const apartment_no = userObject.apartment_no;
    const phone_no = userObject.phone_no;
    let [pitch,setPitch]=useState("");
    const [users, setUsers] = React.useState([]);
    let votedcandidate=userObject.name
    
    useEffect(() => {
        getUsers();
    },[]);
    const navigate=useNavigate();
    
    const handleclick=async (a,b)=>{
        
        votedcandidate = a;
        pitch=b

        let result2=await fetch('http://localhost:5000/voteascand',{
            method:'POST',
            body:JSON.stringify({name,email,phone_no,apartment_no,votedcandidate,pitch}),
            headers:{
                'Content-Type':'application/json'
            }
        });
        alert("Voted Successfully")
        navigate('/events')
    }

    const getUsers = async () => {
        let result = await fetch('http://localhost:5000/votetocand');
        result = await result.json();
        setUsers(result);   
        console.log(result);
    };

    return(
        <div className="product-list" >
            <h1>Candidates Are Listed Below:</h1>
            <ul>
                <li>S.No</li>
                <li>Name</li>
                <li>Pitch</li>
                <li>Action</li>
            </ul>

            {users.length>0 ? users.map((item, index) => (
                <ul key={item._id}>
                    <li>{index + 1}</li>
                    <li>{item.name}</li>
                    <li>{item.pitch}</li>
                    <li><button onClick={()=>handleclick(item.name,item.pitch)}>VOTE</button></li>
                </ul>
            ))
        : <h1>No User Data Found</h1>}
        </div>

    )
}

export default Votetocand;
